  const hora = (extracted.horario_visita_hora || "").trim();

  // Si no hay fecha O no hay hora, timestamp es null
  if (!fecha || !hora) {
    return [{ json: { horario_visita_timestamp: null } }];
  }

  // Parse fecha DD/MM/YYYY
  const parts = fecha.split('/');
  if (parts.length !== 3) {
    return [{ json: { horario_visita_timestamp: null } }];
  }

  const day = parseInt(parts[0], 10);
  const month = parseInt(parts[1], 10);
  const year = parseInt(parts[2], 10);

  // Validate date components
  if (!day || !month || !year || month < 1 || month > 12 || day < 1 || day > 31) {
    return [{ json: { horario_visita_timestamp: null } }];
  }

  // Parse hora H:MMam/pm or HH:MMam/pm
  const horaMatch = hora.match(/^(\d{1,2}):(\d{2})\s*(am|pm)$/i);
  if (!horaMatch) {
    return [{ json: { horario_visita_timestamp: null } }];
  }

  let hours = parseInt(horaMatch[1], 10);
  const minutes = parseInt(horaMatch[2], 10);
  const ampm = horaMatch[3].toLowerCase();

  // Convert to 24-hour format
  if (ampm === 'pm' && hours !== 12) {
    hours += 12;
  } else if (ampm === 'am' && hours === 12) {
    hours = 0;
  }

  // âœ… FIX CRÃTICO: Crear ISO string con timezone EXPLÃCITO de Lima (-05:00)
  try {
    // Construir string ISO con offset de Lima
    const limaDateString = `${year}-${month.toString().padStart(2, '0')}-${day.toString().padStart(2,
  '0')}T${hours.toString().padStart(2, '0')}:${minutes.toString().padStart(2, '0')}:00-05:00`;

    // JavaScript Date convierte automÃ¡ticamente a UTC
    const dateObj = new Date(limaDateString);

    // Validar que la fecha es vÃ¡lida
    if (isNaN(dateObj.getTime())) {
      return [{ json: { horario_visita_timestamp: null } }];
    }

    // Retornar ISO string en UTC (ya convertido correctamente)
    return [{ json: { horario_visita_timestamp: dateObj.toISOString() } }];

